{% if doc.party_type == "Supplier" %}
	{% set pt = "Supplier" %}
{% elif doc.party_type == "Customer" %}
	{% set pt = "Customer" %}
{% endif %}

<!–– TOP SECTION ––>

<div style="position: relative; top: 0.0cm; margin-left: 0.0cm; margin-right: 0.0cm;">
	<div style="width: 21.5cm; height: 27.8cm;">

		<!–– 1st LINE ––>

		<span style="top: 1cm; left: 0.7cm; position: absolute;">
			NOM:
		</span>
		<span style="top: 1cm; left: 4.3cm; position: absolute;">
			{{doc.party_name}}
		</span>
		<span style="top: 1cm; left: 13.2cm; position: absolute;">
			{{ frappe.utils.formatdate(doc.reference_date) or '' }}
		</span>
		<span style="top: 1cm; left: 17cm; position: absolute;">
			{{ doc.reference_no }}
		</span>

		<!–– PI / SI / JV LINE(s) ––>

		<span style="top: 2.5cm; left: 0.7cm; position: absolute;">
			{% if doc.allocate_payment_amount %}
				{% for row in doc.references %}
					{% if row.reference_doctype == "Purchase Invoice" %}
						<div>
							{{ frappe.db.get_value(row.reference_doctype,
							row.reference_name, "bill_date") or '' }}
						</div>
					{% endif %}
					{% if row.reference_doctype == "Sales Invoice" or row.reference_doctype == "Journal Entry" %}
						<div>
							{{ frappe.db.get_value(row.reference_doctype,
							row.reference_name, "posting_date") or '' }}
						</div>
					{% endif %}
				{%- endfor -%}
			{% else %}
				N/A
			{% endif %}
		</span>
		<span style="top: 2.5cm; left: 4.0cm; position: absolute;">
			{% if doc.allocate_payment_amount %}
				{% for row in doc.references %}
					{% if row.reference_doctype == "Purchase Invoice" %}
						<div>
							{{ frappe.db.get_value(row.reference_doctype,
							row.reference_name, "bill_no") or '' }}
						</div>
					{% endif %}
					{% if row.reference_doctype == "Sales Invoice" or row.reference_doctype == "Journal Entry" %}
						<div>
							{{ frappe.db.get_value(row.reference_doctype,
							row.reference_name, "name") or '' }}
						</div>
					{% endif %}
				{%- endfor -%}
			{% else %}
				N/A
			{% endif %}
		</span>
		<span style="top: 2.5cm; right: 9.6cm; position: absolute;">
			{% if doc.allocate_payment_amount %}
				{% for row in doc.references %}
					{% if row.reference_doctype == "Purchase Invoice" %}
						<div align=right>
							{{ row.get_formatted("total_amount") or '0.000' }}
						</div>
					{% endif %}
					{% if row.reference_doctype == "Sales Invoice" or row.reference_doctype == "Journal Entry" %}
						<div align=right>
							{{ row.get_formatted("total_amount") or '0.000' }}
						</div>
					{% endif %}
				{%- endfor -%}
			{% else %}
				N/A
			{% endif %}
		</span>
		<span style="top: 2.5cm; right: 5.6cm; position: absolute;">
			{% if doc.allocate_payment_amount %}
				{% for row in doc.references %}
					{% if row.reference_doctype == "Purchase Invoice" %}
					<div align=right>
						{{ frappe.db.get_value(row.reference_doctype,
						row.reference_name, "discount_amount") or '0.000' }}
					</div>
					{% endif %}
					{% if row.reference_doctype == "Sales Invoice" or row.reference_doctype == "Journal Entry" %}
					<div align=right>
						0.000
					</div>
					{% endif %}
				{%- endfor -%}
			{% else %}
				N/A
			{% endif %}
		</span>
		<span style="top: 2.5cm; right: 1.6cm; position: absolute">
			{% if doc.allocate_payment_amount %}
				{% for row in doc.references %}
					{% if row.reference_doctype == "Purchase Invoice" %}
					<div align=right>
						{{ row.get_formatted("allocated_amount") or '0.000' }}
					</div>
					{% endif %}
					{% if row.reference_doctype == "Sales Invoice" or row.reference_doctype == "Journal Entry" %}
					<div align=right>
						{{ row.get_formatted("allocated_amount") or '0.000' }}
					</div>
					{% endif %}
				{%- endfor -%}
			{% else %}
				N/A
			{% endif %}
		</span>
		<span style="top: 8.4cm; right: 9.6cm; position: absolute;">
			{{ doc.get_formatted("paid_amount") }}
		</span>
		<span style="top: 8.4cm; right: 5.6cm; position: absolute;">
			{% if doc.allocate_payment_amount %}	
				{% set discount_total = 0 %}
				{% set glob={} %}
				{% for row in doc.references %}
					{% if row.reference_doctype == "Purchase Invoice" %}
						{% set discount_line = frappe.db.get_value(row.reference_doctype, 
						row.reference_name, "discount_amount") %}
						{% set discount_total = discount_total + discount_line %}
						{% set _ = glob.update({'discount': discount_total}) %}
					{% endif %}
					{% if row.reference_doctype == "Sales Invoice" or row.reference_doctype == "Journal Entry" %}
						{% set _ = glob.update({'discount': '0.000'}) %}
					{% endif %}
				{% endfor %}
				$ {{ glob.discount }}
			{% else %}
				N/A
			{% endif %}
		</span>
		<span style="top: 8.4cm; right: 1.6cm; position: absolute;">
			{{ doc.get_formatted("paid_amount") }}
		</span>

<!–– MIDDLE SECTION ––>

		<span style="top: 11.2cm; right: 1.6cm; position: absolute;">
			{{ frappe.utils.formatdate(doc.reference_date) or '' }}
		</span>
		<span style="top: 11.6cm; right: 1.6cm; position: absolute;">
			MM JJ AAAA
		</span>
		<span style="top: 12.3cm; left: 4.3cm; position: absolute;
			display: block; width: 15.0cm;
			line-height: 0.5cm; word-wrap: break-word;">
			{{frappe.utils.money_in_words(doc.paid_amount, main_currency=doc.paid_to_account_currency)}}
		</span>
		<span style="top: 15cm; left: 4.3cm; position: absolute;">
			{{doc.party_name}}
		</span>
		<span style="top: 15.5cm; left: 4.3cm; position: absolute;">
                        {% set pr = "Customer" if doc.payment_type == "Receive" else "Supplier" %}
			{% set ad_name = frappe.get_all("Dynamic Link", filters={"link_doctype": pr, "link_name": doc.party, 'parenttype': 'Address'}, fields=["parent"]) %}
			{% if ad_name %}
                        	{% set address = frappe.get_doc('Address', ad_name[0].parent ) %}
				<div>
				{{ address.address_line1 }}
				</div>
				{{ address.city }} 
                                {{ address.state }} {{ address.pincode }}
				</div>
			{% endif %}
		</span>
		<span style="top: 12.8cm; right: 1.6cm; position: absolute;">
			{{doc.get_formatted("paid_amount")}}
		</span>

<!–– BOTTOM SECTION ––>

		<span style="top: 19cm; left: 0.7cm; position: absolute;">
			NOM:
		</span>
		<span style="top: 19cm; left: 4.3cm; position: absolute;">
			{{doc.party_name}}
		</span>
		<span style="top: 19cm; left: 13.2cm; position: absolute;">
			{{ frappe.utils.formatdate(doc.reference_date) or '' }}
		</span>
		<span style="top: 19cm; left: 17cm; position: absolute;">
			{{ doc.reference_no }}
		</span>
		<span style="top: 21cm; left: 0.7cm; position: absolute;">
			{% if doc.allocate_payment_amount %}
				{% for row in doc.references %}
					{% if row.reference_doctype == "Purchase Invoice" %}
						<div>
							{{ frappe.db.get_value(row.reference_doctype,
							row.reference_name, "bill_date") or '' }}
						</div>
					{% endif %}
					{% if row.reference_doctype == "Sales Invoice" or row.reference_doctype == "Journal Entry" %}
						<div>
							{{ frappe.db.get_value(row.reference_doctype,
							row.reference_name, "posting_date") or '' }}
						</div>
					{% endif %}
				{%- endfor -%}
			{% else %}
				N/A
			{% endif %}
		</span>
		<span style="top: 21cm; left: 4.0cm; position: absolute;">
			{% if doc.allocate_payment_amount %}
				{% for row in doc.references %}
					{% if row.reference_doctype == "Purchase Invoice" %}
						<div>
							{{ frappe.db.get_value(row.reference_doctype,
							row.reference_name, "bill_no") or '' }}
						</div>
					{% endif %}
					{% if row.reference_doctype == "Sales Invoice" or row.reference_doctype == "Journal Entry" %}
						<div>
							{{ frappe.db.get_value(row.reference_doctype,
							row.reference_name, "name") or '' }}
						</div>
					{% endif %}
				{%- endfor -%}
			{% else %}
				N/A
			{% endif %}
		</span>
		<span style="top: 21cm; right: 9.6cm; position: absolute;">
			{% if doc.allocate_payment_amount %}
				{% for row in doc.references %}
					{% if row.reference_doctype == "Purchase Invoice" %}
						<div align=right>
							{{ row.get_formatted("total_amount") or '0.000' }}
						</div>
					{% endif %}
					{% if row.reference_doctype == "Sales Invoice" or row.reference_doctype == "Journal Entry" %}
						<div align=right>
							{{ row.get_formatted("total_amount") or '0.000' }}
						</div>
					{% endif %}
				{%- endfor -%}
			{% else %}
				N/A
			{% endif %}
		</span>
		<span style="top: 21cm; right: 5.6cm; position: absolute;">
			{% if doc.allocate_payment_amount %}
				{% for row in doc.references %}
					{% if row.reference_doctype == "Purchase Invoice" or row.reference_doctype == "Sales Invoice"%}
					<div align=right>
						{{ frappe.db.get_value(row.reference_doctype,
						row.reference_name, "discount_amount") or '0.000' }}
					</div>
					{% endif %}
					{% if row.reference_doctype == "Journal Entry" %}
					<div align=right>
						0.000
					</div>
					{% endif %}
				{%- endfor -%}
			{% else %}
				N/A
			{% endif %}
		</span>
		<span style="top: 21cm; right: 1.6cm; position: absolute;">
			{% if doc.allocate_payment_amount %}
				{% for row in doc.references %}
					{% if row.reference_doctype == "Purchase Invoice" %}
					<div align=right>
						{{ row.get_formatted("allocated_amount") or '0.000' }}
					</div>
					{% endif %}
					{% if row.reference_doctype == "Sales Invoice" or row.reference_doctype == "Journal Entry" %}
					<div align=right>
						{{ row.get_formatted("allocated_amount") or '0.000' }}
					</div>
					{% endif %}
				{%- endfor -%}
			{% else %}
				N/A
			{% endif %}
		</span>
		<span style="top: 26.8cm; right: 9.6cm; position: absolute;">
			{{ doc.get_formatted("paid_amount") }}
		</span>
		<span style="top: 26.8cm; right: 5.6cm; position: absolute;">
			{% if doc.allocate_payment_amount %}	
				{% set discount_total = 0 %}
				{% set glob={} %}
				{% for row in doc.references %}
					{% if row.reference_doctype == "Purchase Invoice" or row.reference_doctype == "Sales Invoice" %}
						{% set discount_line = frappe.db.get_value(row.reference_doctype, 
						row.reference_name, "discount_amount") %}
						{% set discount_total = discount_total + discount_line %}
						{% set _ = glob.update({'discount': discount_total}) %}
					{% endif %}
					{% if row.reference_doctype == "Journal Entry" %}
						{% set _ = glob.update({'discount': '0.00'}) %}
					{% endif %}
				{% endfor %}
				$ {{ glob.discount }}
			{% else %}
				N/A
			{% endif %}
		</span>
		<span style="top: 26.8cm; right: 1.6cm; position: absolute;">
			{{ doc.get_formatted("paid_amount") }}
		</span>
	</div>
</div>
